void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float _Scale = 1.0;
    float _Speed = 10.0;
    float _Frequency = -45.0;

    vec2 mouseOffset =  iMouse.xy / iResolution.xy;
     if (mouseOffset.x == 0.0 && mouseOffset.y == 0.0)
    {
        mouseOffset.x = 0.5;
        mouseOffset.y = 0.5;
    }
    float amplitude = 1.0 - distance(fragCoord.xy / iResolution.xy,mouseOffset);



	vec2 uv = fragCoord.xy / iResolution.xy;
//	uv = map (uv, vec2(0,0),vec2(1,1),vec2(-1,-1),vec2(1,1));
    uv = 2.0*uv-1.0;
    vec2 mouseOffsetMapped = 2.0 * mouseOffset - 1.0;
    //float offset = ((uv.x-mouseOffsetMapped.x) *(uv.x-mouseOffsetMapped.x )) + ((uv.y - mouseOffsetMapped.y)*(uv.y  - mouseOffsetMapped.y));
    //better written as:
    float offset = length(uv-mouseOffsetMapped);
    offset*=offset;
    float value = _Scale * sin(iTime*_Speed+offset*_Frequency);
    uv.y= value;


    uv.y= value * pow(amplitude,8.0);
    fragColor = vec4(uv.y,uv.y,uv.y,1.0);

}
