// BY Bart Knuiman

vec2 rotate( float ang, vec2 v )
{
    float s = sin(ang);
    float c = cos(ang);
    return vec2( c*v.x-s*v.y, s*v.x+c*v.y );
}

float heart( vec2 uv, float radius )
{
    uv.x = abs(uv.x);
    uv.x *= .9;
    uv = rotate(0.9, uv);
    uv.y*=.55;
    if ( uv.y < 0. ) uv.y *= .9;
    float kr = pow(abs(sin(iTime*2.)),30.)*radius*.24;
    float kr2 = pow(abs(sin(iTime*2.+.5)),30.)*radius*.24;
    return length(uv)-(radius + kr + kr2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv  = fragCoord-iResolution.xy*.5;
    vec2 tuv = uv/iResolution.xy;

    float d  = heart( uv, iResolution.x*.12);
    float AA = 1.;
    float df = smoothstep( -AA, AA, d );

    float colHeart = mix( 0., 1.0, pow( abs(d)/50., .15 ) );
    vec3 col 	   = mix( vec3(colHeart,0.,0.), vec3(pow( 1.-length(tuv), .4 )), df );

    fragColor = vec4(col ,1.0);
}
