const float speed = 1.5;
const float widthFactor = 4.0;

vec3 calcSine(vec2 uv, 
              float frequency, float amplitude, float shift, float offset,
              vec3 color, float width, float exponent)
{
    float angle = iTime * speed * frequency + (shift + uv.x * 1.0 * width) * 6.2831852;

    float y = sin(angle) * amplitude + offset;

    float scale = pow(smoothstep(width, width-0.03, distance(y, uv.y)), exponent);
    scale *= 3.0 * width + 0.2 / pow(smoothstep(10.0 * width, 0.1*width, distance(y, uv.y)), 190.);

    return color * scale;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    color += calcSine(uv, 1.5, 0.25, 1.2, 0.5, vec3(0.1, 0.5, 1.0), 0.13, 1.0);
    color += calcSine(uv, 1.0, 0.25, 2.3, 0.5, vec3(0.1, 0.5, 1.0), 0.1, 1.0);
    color += calcSine(uv, 0.3, 0.25, 12.0, 0.5, vec3(0.1, 0.5, 1.0), 0.08, 1.0);

    fragColor = vec4(color,1.0);
}
