void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec2 uv = fragCoord.xy / iResolution.xy;
    highp float pi = 3.14159265;
    fragColor = vec4(0.5 + sin(iTime*30.0 + (uv.x + uv.y) * 27.0 + 2.0*pi/3.0)/2.0,
                     0.5 + sin(iTime*30.0 + (uv.x + uv.y) * 27.0 + 2.0 * 2.0 * pi / 3.0)/2.0,
                     0.5 + sin(iTime*30.0 + (uv.x + uv.y) * 27.0)/2.0,
                     0);

}
