void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    float ry = 0.125; float rx = ry/ratio;
    float deltay = 0.01; float deltax = deltay/ratio;
    float centx = cos(iTime*2.)*rx;
    float centy = sin(iTime*2.)*ry;
    uv.xy += vec2 (centx, centy);
    uv.xy -= vec2(0.5,0.5);
    float xr = uv.x*cos(iTime/2.) - uv.y*sin(iTime/2.)/ratio;
    float yr = uv.x*sin(iTime/2.) + uv.y*cos(iTime/2.)/ratio;
    uv.x = xr; uv.y = yr;
    uv.xy += vec2(0.5,0.5);

	fragColor = texture(iChannel0, uv);
    fragColor.x *= 0.5+sin(iTime)/2.0;
    fragColor.y *= 0.5+cos(iTime)/2.0;
    fragColor.z *= 0.5+(cos(iTime+1.5)*sin(iTime-1.5))/2.0;

    if (fragColor.x < 0.01 && fragColor.y < 0.01 && fragColor.z < 0.01)
    {
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
	    uv.xy -= vec2(0.5,0.5);
        float xr = uv.x*cos(iTime/2.) + uv.y*sin(iTime/2.)/ratio;
        float yr = -uv.x*sin(iTime/2.) + uv.y*cos(iTime/2.)/ratio;
        uv.x = xr; uv.y = yr;
	    uv.xy += vec2(0.5,0.5);
        //uv.xy *= 0.9;
        fragColor = texture(iChannel2, uv);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    float ry = 0.25; float rx = ry/ratio;
    float deltay = 0.01; float deltax = deltay/ratio;
    float lisx = sin(5.0*iTime/5.0);
    float centx = 0.5;
    float targx = centx + lisx * rx;
    float colx = (uv.x > targx-deltax && uv.x < targx+deltax) ? 1.0 : 0.0;
    float lisy = cos(3.0*iTime/5.0);
    float centy = 0.5;
    float targy = centy + lisy * ry;
    float coly = (uv.y > targy-deltay && uv.y < targy+deltay) ? 1.0 : 0.0;
    float col = colx*coly;
    // If we do nothing, pen is a square
    // If we do something, it can be... a circle ... a gradient circle
    float dist = (uv.x - targx) * (uv.x - targx)  + (uv.y - targy) * (uv.y - targy);
    vec4 oldColor = texture(iChannel0, uv);
    float dist2 = deltay*deltay*4. - dist;
    if (dist2 > 0.)
    {
        fragColor = vec4(max (1.0 * dist2 / (deltay*deltay*2.), oldColor.x));
    }
    else
    {
        fragColor = oldColor*0.996;
        // Floor effect, if needed (value > 0.0). Snake queue effect.
        if (fragColor.x < 0.15)
            fragColor = vec4(0.0);
    }
}

#define PI 3.1415926
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = fragCoord.x/fragCoord.y;
    uv *= 10.0;
    float grad1 = cos(uv.x+iTime);
    float grad2 = cos(uv.y+iTime);
    float grad3 = cos(sqrt(10.*(grad1*grad1 + grad2*grad2)+1.) + 0.2*iTime);
    float grad4 = cos(uv.x+uv.y+iTime);
    float grad41 = cos(uv.x*sin(iTime/2.) + uv.y*cos(iTime/3.)+iTime);
    float grad5 = grad1 + grad2 + grad3 + grad4 + grad41;

    //float grad5 = grad41;


    fragColor = vec4(.2+cos(grad5), .2+cos(grad5+.5*PI), .1 + cos(grad5+1.0*PI), 1.);
}
