vec2 hexagon(in vec2 p) 
	{
	vec2 q = vec2( p.x*2.0*0.5773503, p.y + p.x*0.5773503 );
	vec2 pi = floor(q), pf = fract(q);
	float v = mod(pi.x + pi.y, 3.0);
	float ca = step(1.0,v), cb = step(2.0,v);
	vec2  ma = step(pf.xy,pf.yx);

	return vec2( pi + ca - cb*ma);
	}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.yx / iResolution.y;

    vec2 hex = hexagon(uv*20.0);

    vec3 col = vec3((uv.x/mod(iTime, 5.0))*0.125,(uv.y/mod(iTime, 5.0))*0.125, 0.0);
	col*= hex.x;



        fragColor = vec4(col, 0.0);


}
