// Started off as an example of how to set center of 2D rotation got ugly....

//#define STEPPED

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
	vec2 uv = fragCoord / iResolution.xy;

    // Increasingly speed up timebase
    float tr = pow(t,1.+t/100.);

#ifdef STEPPED
    // simple loop setting center of rotation between 9 grid points
    float ts = mod(tr,18.);
         if (ts<2.)  uv -= vec2(.5, .5);
    else if (ts<4.)  uv -= vec2(.9, .5);
    else if (ts<6.)  uv -= vec2(.9, .1);
    else if (ts<8.)  uv -= vec2(.5, .1);
    else if (ts<10.) uv -= vec2(.1, .1);
    else if (ts<12.) uv -= vec2(.1, .5);
    else if (ts<14.) uv -= vec2(.1, .9);
    else if (ts<16.) uv -= vec2(.5, .9);
    else             uv -= vec2(.9, .9);
#else
    // continuously moving center is evil
    uv -= .5-.5*vec2(sin(tr), cos(tr));    
#endif    

    // Spin texture back and forth
    float tw = .1*sin(tr)*t;
    vec3 scT = vec3(cos(tw),-sin(tw),0);
               scT.z = -scT.y;

    // creating standard 2D rotation matrix from swizzled vector
    mat2 rot2D = mat2( scT.xyzx);

    fragColor = texture(iChannel0, rot2D * uv );
}
