float drawAlpha(Ray r, vec2 p, float width)
{
    float d = dist(r, p);
    return mix(1.,0.,clamp(d*iResolution.y/2. - width, -1., 1.)*.5+.5);
}

void draw(Ray r, vec2 p, float width, vec3 fgCol, inout vec3 bgCol)
{
    bgCol = mix(bgCol, fgCol, drawAlpha(r, p, width));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 buf = texelFetch(iChannel0, ivec2(fragCoord), 0);

    vec3 c = buf.rgb;

    c = sqrt(c);

    fragColor = vec4(c, 1.);
}

#define lineWidth 	(-.5)

vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int N = int(iResolution.y);
    float dy = 1. / (float(N)-1.);
    float k = float(iFrame%N) *dy;

    //float r = .38;
    //Circle c = Circle(vec2(0.15,0.), r);
    Circle c = Circle(vec2(0.), .5);

    Circle c0 = Circle(getPt(c, k), c.rad*abs(sin(k*PI*2.)));

    //Circle c0 = Circle(getPt(c, k), c.rad*2.*sin(k*PI));


    vec2 p = (fragCoord -iResolution.xy*.5)/iResolution.y*2.;
    vec3 col = vec3(0.);
    float d;

    d = dist(c0,p);
    vec3 cc = hsv2rgb_smooth(vec3(k,1.,1.));
    col = mix(col, cc, mix(1.,0.,clamp(d*iResolution.y/4. - lineWidth, -1., 1.)*.5+.5));

    col *= 20.;

    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    //fragColor += vec4(col*.1, 1.);
    fragColor = (fragColor*float(iFrame) + vec4(col, 1.))/float(iFrame+1);
}

#define PI	3.1415926535


struct Circle
{
    vec2 ctr;
    float rad;
};

struct Ray
{
    vec2 org;
    vec2 dir;
};

struct Segment
{
    vec2 A;
    vec2 B;
};

bool intersect(Circle c, Ray r, out vec2 t)
{
    float A = dot(r.dir, r.dir);
    float B = dot(r.dir, -c.ctr+r.org);
    float C = dot(c.ctr, c.ctr) +dot(r.org, r.org) -2.*dot(c.ctr, r.org) - c.rad*c.rad;

    float delta = B*B-A*C;

    if(delta < 0.0)
    	return false;

    t = (vec2(-B) + vec2(sqrt(delta))*vec2(-1.,1.))/A;
    return true;
}

vec2 getPt(Ray r, float t)
{
    return r.org + t*r.dir;
}

vec2 getPt(Circle c, float t)
{
    return c.ctr + c.rad*vec2(cos(t*PI*2.), sin(t*PI*2.));
}

float dist(Ray r, vec2 p)
{
    return abs(dot(r.dir.yx*vec2(-1., 1.), p-r.org)/dot(r.dir, r.dir));
}

float dist(vec2 p0, vec2 p1)
{
    return length(p1-p0);
}

float dist(Circle c, vec2 p)
{
    return abs(length(c.ctr-p)-c.rad);
}

float dist(Segment s, vec2 p)
{
	vec2 pa = p-s.A, ba = s.B-s.A;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );

}
