void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float bx=6.0+3.0*sin(iTime);
    float bw=0.04+0.01*cos(iTime*10.0);
    float col=0.0;
    float bd=1.0/bx;
    float cpx=mod(uv.x-2.0*sin(iTime/2.0),bd);
    float cpy=mod(uv.y-2.0*cos(iTime/2.0),bd);
    float fx=1.0/(bw/2.0);
	if(cpx<(bw/2.0))
    {
        col=1.0-(cpx*fx);
    }
    else if(cpx>(bd-bw/2.0))
    {
        col=1.0-(bd-cpx)*fx;
    }
    if(cpy<(bw/2.0))
    {
        col+=1.0-(cpy*fx);
    }
    else if(cpy>(bd-bw/2.0))
    {
        col+=1.0-(bd-cpy)*fx;
    }
    // Output to screen
    fragColor = vec4(col,col,col,1.0);
}
