void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 crds =(2.0* fragCoord.xy-iResolution.xy)/iResolution.y;
	vec3 mous=vec3((2.0* iMouse.xy-iResolution.xy)/iResolution.y,1.0);
	vec3 coords=vec3(crds,1.0);
	float co=cos(iTime);
	float si=sin(iTime);
    coords = vec3(co*coords.x-si*coords.y,si*coords.x+co*coords.y,1.0);
    coords=mod(mous*mous,coords*coords);
    fragColor = vec4(6.0*coords+3.0*coords*coords,1.0);
}
