void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;

    for (int i = 0; i < 5; i++) {
        vec2 puv = (mod(abs(uv),1.)-.5)-uv;
        uv -= normalize(puv+cos(uv*10.)*.1)*cos(length(uv)+iTime);
    }

	fragColor = vec4(normalize(uv)*.5+.5,
                     cos(length(uv)+iTime) ,1.0);
    fragColor = texture(iChannel0, uv);
}
