#define NUM_PARTICLES 200
#define PIXEL_SCALE_HALF 1.0

vec2 uv;
vec2 uvSquare;

float drawParticle(vec3 particlePosVel)
{
    vec2 toParticle = (particlePosVel.xy - uv) * iResolution.xy;
    vec2 toParticleAbs = abs(toParticle);

    bvec2 particleSquare = lessThan(toParticleAbs, vec2(PIXEL_SCALE_HALF));

    float particleness = float(all(particleSquare));
    if(particleSquare.x && toParticle.y < 0.0)
    {
		float trail = 700.* particlePosVel.z / (-toParticle.y);
        particleness += min(trail, 1.0) * 0.8f;
    }

    return particleness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    uv = fragCoord/iResolution.xy;

  	fragColor = texture(iChannel0, uv);
    if(fragColor.a == 0.0)
        fragColor = vec4(0.0,0.0,0.0,1.0);

    vec3 fallingPixels = vec3(0.0);
    for (int i=0; i<NUM_PARTICLES; ++i)
    {
        vec3 particlePosVel = texelFetch(iChannel1, ivec2(i, 0), 0).xyz;
        vec3 particleColor = texelFetch(iChannel1, ivec2(i, 1), 0).rgb;
        fragColor.rgb = mix(fragColor.rgb, particleColor, drawParticle(particlePosVel));
    }
}

#define NUM_PARTICLES 200
#define PIXEL_SCALE_HALF 1.0

float rand(float n)
{
    return fract(sin(n) * 43758.5453123);
}
vec2 particleSpawnPos(int frame, int particleIdx)
{
    float seed = float(frame) + float(particleIdx) / float(NUM_PARTICLES);
    vec2 pos;
    pos.x = rand(seed);
    pos.y = rand(pos.x + seed);
    return floor(pos * iResolution.xy + vec2(0.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    if (iFrame < 10)
    {
        fragColor = texture(iChannel2, uv);
        return;
    }

    fragColor = texture(iChannel0, uv);

    for (int i=0; i<NUM_PARTICLES; ++i)
    {
        vec2 particlePos = texelFetch(iChannel1, ivec2(i, 0), 0).xy;
        if(particlePos.y <= 0.0f) // Particle dies, so does a pixel in the background.
        {
            vec2 particleSpawnSite = particleSpawnPos(iFrame, i);
            vec2 toSpawnSite = abs(particleSpawnSite - fragCoord);
            if (all(lessThan(toSpawnSite, vec2(PIXEL_SCALE_HALF))) )
            {
                fragColor.a = 0.0; // Mask out via alpha, so next pass can still write
                return;
            }
        }

    }
}

#define NUM_PARTICLES 200
#define PIXEL_SCALE_HALF 1.0

float rand(float n)
{
    return fract(sin(n) * 43758.5453123);
}
vec2 particleSpawnPos(int frame, int particleIdx)
{
    float seed = float(frame) + float(particleIdx) / float(NUM_PARTICLES);
    vec2 pos;
    pos.x = rand(seed);
    pos.y = rand(pos.x + seed);
    return floor(pos * iResolution.xy + vec2(0.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 10)
    {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
        return;
    }

    ivec2 fragCoordInt = ivec2(fragCoord.x, fragCoord.y);
    if(fragCoordInt.y > 2 || fragCoordInt.x >= NUM_PARTICLES)
    	return;

    int particleIdx = fragCoordInt.x;

    vec3 particlePos =   texelFetch(iChannel0, ivec2(particleIdx, 0), 0).xyz;
    vec3 particleColor = texelFetch(iChannel0, ivec2(particleIdx, 1), 0).rgb;

    // just do all the simulation here and decide what we're actually writing in the end - easier that way

    // Particle dead, respawn new
    if(particlePos.y <= 0.0f)
    {
		particlePos.xy = particleSpawnPos(iFrame, particleIdx) / iResolution.xy;
        particlePos.z = 0.0;
        // Would actually like average color of "pixel" area, but sampled pos should do as well..
        particleColor = textureLod(iChannel1, particlePos.xy, 0.0).rgb;
    }
    // Animate particle, could 
    else
    {
        particlePos.z += iTimeDelta*0.008;
		particlePos.y -= particlePos.z; // todo: square speed, falling and stuff.
    }


    // We are a pos pixel!
    if(fragCoordInt.y == 0)
        fragColor = vec4(particlePos, 1.0);
    // We are a color pixel
    else
        fragColor = vec4(particleColor, 1.0);
}
