#define TWOPI 6.28318530718

//comment one or the other to change which source to look at (or use both to see the sum)
#define SOUNDCLOUD
//#define MIC


//hsl to rgb conversion, assuming that s & l = 1.0
vec3 getColor(float amt) {
	float colDeg = amt * TWOPI;
    float x = 1.0 - abs(mod(colDeg/radians(60.0), 2.0) -1.0);
    if(colDeg < radians(60.00)){return vec3(1.0,	x, 		0.0);}
    if(colDeg < radians(120.0)){return vec3(x,		1.0, 	0.0);}
    if(colDeg < radians(180.0)){return vec3(0.0, 	1.0, 	x);}
    if(colDeg < radians(240.0)){return vec3(0.0, 	x, 		1.0);}
    if(colDeg < radians(300.0)){return vec3(x, 		0.0, 	1.0);}
    return vec3(1.0, 0.0, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.);

    #ifdef SOUNDCLOUD
    col += getColor(texture(iChannel0, uv).x);
    #endif
	#ifdef MIC
    col += getColor(texture(iChannel1, uv).x);
    #endif
    // Output to screen
    fragColor = vec4(col,1.0);
}

//how quickly to travel towards the current amp if it is greater than old
#define SLIDE_UP 1.0
//how quickly to travel towards the current amp if it is less than old
#define SLIDE_DOWN 40.0

//modify to set the depth of history to be kept
#define BANDCOUNT 100.0

vec3 slideAmp(vec2 uv) {
    //get the past frame's value
   	float ampOld = texture(iChannel1, uv).x;

    //get the new value
    float ampCurr = texture(iChannel0, vec2(uv.x, 0.25)).x;

    //control whether up or down is used
    float amount = ampCurr > ampOld ? 1.0 : 0.0;

	// calculate slide down
	float down_amt = max(1.0, abs(SLIDE_DOWN));
	float sdown = 1.0 / down_amt;
	vec3 down = vec3(ampOld + ((ampCurr - ampOld) * sdown));

	// calculate slide up
	float up_amt = max(1.0, abs(SLIDE_UP));
	float sup = 1.0 / up_amt;
	vec3 up = vec3(ampOld + ((ampCurr - ampOld) * sup));

    return mix(down, up, vec3(amount));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //get normalized coords
    vec2 uv = fragCoord / iResolution.xy;

    float band = floor(uv.y * BANDCOUNT);

    vec2 uv2 = vec2(uv.x, band / BANDCOUNT);

    vec3 col = vec3(0.0);

    if(band == 0.0) {
		col += slideAmp(uv2);
    } else {
		vec2 uv3 = vec2(uv.x, (band - 1.0) / BANDCOUNT);
        col += texture(iChannel1, uv3).xyz;
    }

    vec3 col2 = vec3((fragCoord.y - 1.0) / iResolution.y);
    fragColor = vec4(col,1.0);
}

