// Code by Flopine
// Thanks to wsmind, leon, lsdlive, XT95, lamogui for teaching me <3 :)

mat2 rot (float a)
{float c = cos(a); float s = sin(a); return mat2 (c,s,-s,c);}

vec2 moda (vec2 uv, float per)
{
    float a = atan(uv.y,uv.x);
    float l = length(uv);
    a = mod(a-per/2.,per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

float circle (vec2 uv, float r)
{return smoothstep(r,r+0.01,length(uv));}

vec3 map (vec2 uv)
{
    float r = 0.03;
    float c = circle(uv,r);
    for (int i=0;i<3;i++)
    {
        uv = moda(uv, 3.141592/6.);
        uv = abs(uv);
        uv *= rot(3.141592/6.);
        r += 0.06;
 		uv.x -= 0.45;
        uv *= rot(iTime*0.3);
        c *=circle(uv,r);
    }
    return ((1.-c)*mix(vec3(1.,0.,0.),vec3(0.3,0.,1.),length(uv)*2.5));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 col = map(uv*1.6);

    // for edges
    //if (col.b <= 0.5) col = vec3(0.,uv.y+0.3,uv.y+1.1);

    if (col.b <= 0.0) col = vec3(0.,uv.y+0.3,uv.y+1.1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
