//#define VIEW_POSITION_BUFFER
//#define VIEW_CHUNK_BUFFER

//NUM_BODIES must be less than or equal to iResolution.x and must be changed in all tabs.
#define NUM_BODIES 800

#define BODY_RADIUS 2.0

vec2 invRes = vec2(0);

vec2 getBody(int id)
{
    return texture(iChannel1, vec2(id, 0.0)*invRes.x).xy;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    invRes = 1.0 / iResolution.xy;
    vec2 res = iResolution.xy / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.y;

    float d = 1e6;

    for(int i = 0;i < NUM_BODIES;i++)
    {
        vec2 body = getBody(i) - uv;
		d = min(d, dot(body,body));
    }

    d = sqrt(d);

    float px = 1.0/iResolution.y;

    float c = smoothstep(BODY_RADIUS*px - px, BODY_RADIUS*px, d);

	fragColor = vec4(vec3(c),1.0);

    #ifdef VIEW_POSITION_BUFFER
    	fragColor = texture(iChannel1, uv / res / vec2(1,64));
    #endif
    #ifdef VIEW_CHUNK_BUFFER
    	fragColor = texture(iChannel0, uv / res) / 16.0;
    #endif
}

//Acceleration Chunk Buffer

//fragCoord.x = Body ID
//fragCoord.y = Chunk ID

//fragColor.xy = Chunk Acceleration

//The process of summing up the accelerations is broken up into small parallel chunks which are summed up to get
//the final net acceleration of a body.

#define CLAMP_VIEW

#define NUM_BODIES 800
#define CHUNK_SIZE 32

#define BODY_MASS 0.2
#define MAX_ACCEL 1e4

//Get a body from the backbuffer with its ID (xy = Current pos, zw = Last pos).
vec4 getBody(int id)
{
    return texture(iChannel0, vec2(id,0)/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    if( fragCoord.x > float(NUM_BODIES))
    {
        fragColor = vec4(0);
        return;
    }

    int id = int(fragCoord.x);
    int chunk = int(fragCoord.y);

    vec4 body = getBody(id);

    vec2 acc = vec2(0);

    for(int i = 0;i < CHUNK_SIZE;i++)
    {
        if(i + chunk * CHUNK_SIZE != id)
        {
            vec2 diff = getBody(i + chunk * CHUNK_SIZE).xy - body.xy;

            acc += min(MAX_ACCEL, BODY_MASS / dot(diff, diff)) * normalize(diff);
        }
    }

    fragColor = vec4(acc,0,0);
}

//Body Position Buffer

//fragCoord.x = Body ID

//fragColor.xy = Current Body Position
//fragColor.zw = Last Body Position

//Keep bodies inside the viewport
#define CLAMP_VIEW

#define NUM_BODIES 800
#define CHUNK_SIZE 32

#define TIME_SCALE 0.005

//Get an acceleration chunk from the chunk buffer.
vec2 getChunk(int id, int blk)
{
    return texture(iChannel0, vec2(id, blk)/iResolution.xy).xy;
}

//Get a body from the backbuffer with its ID (xy = Current pos, zw = Last pos).
vec4 getBody(int id)
{
    return texture(iChannel1, vec2(id, 0.0)/iResolution.xy);
}

//Integrate the body's position.
vec4 Integrate(vec4 body, vec2 accel, float delta)
{
    return vec4(2.0*body.xy - body.zw + accel * delta*delta, body.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    if(fragCoord.y > 0.5 || fragCoord.x > float(NUM_BODIES))
    {
        fragColor = vec4(0);
        return;
    }

    vec2 res = iResolution.xy / iResolution.y;

    int id = int(fragCoord.x);

    vec4 body = getBody(id);

    vec2 acc = vec2(0);

    for(int i = 0;i < NUM_BODIES/CHUNK_SIZE;i++)
    {
        acc += getChunk(id, i);
    }

    body = Integrate(body, acc, iTimeDelta * TIME_SCALE);

    //Initialization (iFrame == 0 doesn't seem to work when the page is initially loaded)
    if(iFrame == 0 || iTime < 3.0)
    {
        vec2 rpos = vec2(0);
        float stride = iChannelResolution[2].x;
        rpos.x = mod(float(id), stride);
        rpos.y = floor(float(id) / stride);
        body = texture(iChannel2, rpos / iChannelResolution[2].xy, -99.0).xyxy * res.xyxy;
    }

    #ifdef CLAMP_VIEW
    	body = clamp(body, vec4(0), res.xyxy);
    #endif

    fragColor = body;
}
