void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0,uv);
}

#define lookup(pos) texture(iChannel0,pos).x
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 invResolution = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy * invResolution;

    float prevState = texture(iChannel0,uv).x;

    float e = 0.;
    e += lookup(uv + vec2( 0., 1.) * invResolution);
    e += lookup(uv + vec2( 0.,-1.) * invResolution);
    e += lookup(uv + vec2( 1., 0.) * invResolution);
    e += lookup(uv + vec2(-1., 0.) * invResolution);
    e += lookup(uv + vec2( 1., 1.) * invResolution);
    e += lookup(uv + vec2( 1.,-1.) * invResolution);
    e += lookup(uv + vec2(-1., 1.) * invResolution);
    e += lookup(uv + vec2(-1.,-1.) * invResolution);
    if( e == 2. && prevState == 1. || e == 3. )
    	fragColor= vec4(1.);
    else
    	fragColor= vec4(0.);

    if(iMouse.z > .5)
    	fragColor= max(fragColor,vec4(step(length(iMouse.xy - fragCoord),25.)));
    else if (iFrame < 10)
        fragColor = vec4(step(length(vec2(.5) - uv),.1));
}
