// choose your favorite texture or video in channel 1.
// suggestion:  street image, singer video.

void mainImage( out vec4 O, vec2 U ) { 
    vec2 R = iResolution.xy;
    O = texture(iChannel0, U/R);               // mapping scrampling
    O = O.x>0. ? texture(iChannel1, O.xy)      // choosen texture
               : vec4(sin(30.*iDate.w),0,0,0);   // cursor
}

#define N 8.  // 8/4/2 . size of puzzle = N*N
#define T(i,j) texture(iChannel0, U + vec2(i,j)/N)
#define is0(i,j) T(i,j).z == 0.     

void mainImage( out vec4 O,  vec2 U )
{
    vec2 R = iResolution.xy;
    U /= R;
    if (iFrame<10) {                           // O = permut(u,v) mapping , tile 0 keeps 0
        O = floor(texture(iChannel1,U*N/8.+.5).rrrr*63.+.5);  // permutation tile texture
        O = floor(O*N*N/64.)/N;
        if (O.x>0.)  O.xy = fract(N*U)/N + vec2 ( fract(O.x), floor(O.x)/N );
    } 
    else {                                     // let's play
        O = texture(iChannel0,U);
        vec4 M=iMouse; 
        if (M.xy==vec2(0)) M = vec4(R*fract(4e5*sin(iDate.w*vec2(1,17.7))),1,0); // demo mode
        if (M.z>0.) {                          // mouse click
            vec2 m = ceil(M.xy/R*N) - ceil(U*N);             // tile offset
            if (length(m)<=1. && is0(0,0) ) O = T(m.x,m.y);  // black replaced by clicked neighbor
            else if ( m==vec2(0) && (is0(1,0) ||  is0(-1,0) || is0(0,1) || is0(0,-1))) O-=O; // which gets black
        }
    }  
}
