float distanceToPerimeter(vec2 p, vec2 bounds, float radius)
{
	return length(max(abs(p) - bounds, 0.0)) - radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord - iResolution.xy * 0.5;
    float radius = 20.0;
	vec2 bounds = vec2(140.0 - radius, 90.0 - radius);
	float w = 12.0;
//    float w = sin(atan(p.y, p.x) * 6.0 + iTime * 3.5) * 3.0 + 8.0;
	float wobble = sin(fragCoord.x / 20.0 + iTime * 3.5) + sin(fragCoord.y / 20.0 + iTime * 2.2);
//	float wobble = sin(atan(p.y, p.x) * 6.0 + iTime * 3.5);
//	float wobble = 0.0;
    wobble *= 3.0;
    float d = distanceToPerimeter(p, bounds, radius);
    float c = 0.3 + smoothstep(0.5 + wobble, 1.5 + wobble, d) * 0.7 - smoothstep(w - 0.5 + wobble, w + 0.5 + wobble, d);
	vec2 texCoord = vec2(fragCoord/ iResolution.xy);
    vec4 bgColor = texture(iChannel0, texCoord);
    vec4 frameColor = vec4(1.0, 0.2, 0.5, c);
    fragColor = vec4(mix(bgColor.rgb, frameColor.rgb, frameColor.a), 1.0);
}
