void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ftime = float(iFrame)/50.;
    float stime = float(iFrame)/50.;
	vec2 uv = 2.*(fragCoord.xy/iResolution.xy-0.5)+vec2(sin(ftime),sin(ftime+1.))/16.;
    float colour;
    if (floor(mod(pow(length(uv)*148.,0.5),2.)) ==1.) {

        colour = floor(mod(stime+8.*atan(uv.y, uv.x),2.));

    } else {
        colour = floor(mod(stime+1.+8.*atan(uv.y, uv.x),2.));
    }
	fragColor = vec4(0,colour*(length(uv)),0,1.0);
}
