bool box(vec2 pos, vec4 bounds)
{
    return ((pos.x >= bounds.x) && (pos.x <= bounds.z) && 
            (pos.y >= bounds.y) && (pos.y <= bounds.w));
}


const vec3 blue = vec3(ivec3(0, 114, 206)) / 255.;
const vec3 white = vec3(ivec3(255, 255, 255)) / 255.;
const vec3 gold = vec3(ivec3(0, 0, 0)) / 255.;
const vec3 pole_color = vec3(0.4, 0.4, 0.4);
const vec3 sky = vec3(0.8, 0.9, 1.0);



vec3 scene(vec2 pos)
{
    vec3 color = sky;

	//Pole body	
    color = mix(color, pole_color, float(box(pos, vec4(-12., -20., -12., 10.))));
    //Pole head
    color = mix(color, pole_color, float(box(pos, vec4(-13., 8., -11., 9.))));

    pos.y += sin(-pos.x / 4.0 + floor(mod(iTime,100.0) * 7.0)) * 1.022;

    color = mix(color, blue, float(box(pos, vec4(-10., -2, 12, 6))));
    color = mix(color, gold, float(box(pos, vec4(-10., -2, 12, 2))));
    color = mix(color, white, float(box(pos, vec4(-10., -6, 12, -2))));

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//Get coordinates in the range [-1.0, 1.0]
    vec2 uv = (fragCoord.xy) / iResolution.xy;
    uv = (uv - 0.5) * 2.0; 

    //Adjust for aspect ratio
    vec2 aspect_uv = uv * (iResolution.xy / iResolution.y);

    vec3 pole = vec3(0.);

    vec2 pixel_uv = floor(aspect_uv * 12.);

	fragColor = vec4(scene(pixel_uv) + pole, 1.0);
}
