float sphereIntersect(vec3 rayOrigin, vec3 rayDirection, vec3 center, float radius)
{
 	vec3 rc = rayOrigin - center;
    float c = dot(rc, rc) - (radius*radius);
    float b = dot(rayDirection, rc);
    float d = b*b - c;
    float t = -b - sqrt(abs(d));
    float st = step(0.0, min(t,d));
    return mix(-1.0, t, st);
}

vec3 SphereCenter = vec3(0.0, 0.0, 10.0);
float SphereRadius = 5.0;

vec3 CameraCenter = vec3(0.0, 0.0, -3.0);
float imagePlaneDist= 1.0;

vec3 LightPos = vec3(2.0, 6.0, 0.0);

vec4 purp = vec4(0.5, 0.0, 1.0, 1.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv * 2.0) - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    //SPHERE RENDER
    //ray origin
    vec3 rayOrigin = CameraCenter
        ;
    //image plane pixel
    vec3 imagePlanePixel = vec3(uv.x, uv.y, CameraCenter.z + imagePlaneDist);

    //ray direction
    vec3 rayDir = normalize(imagePlanePixel - rayOrigin);

    //see if sphere intersects
    float value = sphereIntersect(rayOrigin, rayDir, SphereCenter, SphereRadius);


    //LIGHTING
    //get sphere coordinate
    vec3 spherePos = imagePlanePixel + (rayDir * value);

    //calcualte sphere normal
    vec3 sphereNormal = normalize(-(SphereCenter - spherePos));

    //calculate light's effect on pixel
    vec3 light = normalize(LightPos - spherePos);
    float lightValue = dot(sphereNormal, light);

    // Output to screen
    value = clamp(value, 0.0, 1.0);
    value *= lightValue;

    fragColor = vec4(value * purp.x, value * purp.y, value * purp.z, 1.0);
}
