// variant of "helix 2" https://shadertoy.com/view/4sdBW8
//        and "helix 3" https://www.shadertoy.com/view/XdtfW8

void mainImage(out vec4 O, vec2 U) {

    float t = iTime,l,d,a,v;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),     // ray direction
          p = 30./q;                           // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                  // rotation
        l = length(q.xy), a = atan(q.y,q.x), d = a-q.z,
     // d = sin(d),                            // sin: double (opposed)
     // d = mod(d,6.28)-3.14,                  // mod: single
        d = min(v=abs( mod(d    ,6.28) -3.14), 
                  abs( mod(d-2.1,6.28) -3.14)),// double (2pi/3)
        t = sin(iTime) < 0. 
          ? length(vec2(l-4.,d)) - .5          // double-spring
          : length(vec3(l-4.,d,fract(4.*a)-.5)) - .5,  // spheres along double spring
        p += t*D;                              // step forward = dist to obj

    if (d==v) a += 3.14;
    O *= .6+.6*cos(a+vec4(0,23,21,0) );        // https://www.shadertoy.com/view/ll2cDc
}
