void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

#define M 10.0

#define HOOD_WIDTH 1.0 * M
#define HOOD_HEIGHT 1.0 * M

#define MOUSE_SIZE 0.1

#define DASTART 120.0
#define DAEND 120.0

#define AASTART 10.0
#define AAEND 150.0

//#define DASTART 3.0 * M
//#define DARANGE 0.99 * M

//#define AASTART 2.0 * M
//#define AARANGE 1.99 * M


#define DENSITY 0.5
float rand(vec2 co){
  	return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * length(iDate));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 cellWidth = 1.0/iResolution.xy;
   	vec2 uv = fragCoord.xy / iResolution.xy;

	//	sample yourself
    float centerCell = texture(iChannel0, uv).r;

    //	total your neighbors
    float hoodTotal = 0.0;
    for( float yi = -HOOD_HEIGHT; yi <= HOOD_HEIGHT; yi++ )
    {
    	for( float xi = -HOOD_WIDTH; xi <= HOOD_WIDTH; xi++ )
        {
			vec2 xy = vec2(xi,yi) * cellWidth + uv;
            xy = vec2(mod(xy.x, 1.0), mod(xy.y, 1.0) );
            hoodTotal += texture(iChannel0, xy).r;
        }
    }

    //	dont count yourself
    //hoodTotal -= centerCell;

    //	sample the rule
    float c = 0.0;
    if( centerCell == 0.0 )
    {
        if( (hoodTotal >= DASTART) && (hoodTotal <= DAEND) )
        {
        	c = 1.0;
        }
    }
    else if( centerCell == 1.0 )
    {
        if( (hoodTotal >= AASTART) && (hoodTotal <= AAEND) )
        {
        	c = 1.0;
        } 
    }

    fragColor = vec4(vec3(c),1.0);
    if (iMouse.z >=0.0)
    {
        float mc = step( distance(uv,(iMouse.xy/iResolution.xy)), MOUSE_SIZE ) ;
        //fragColor = vec4(step(rand(uv),DENSITY));
    	fragColor = min( vec4(vec3(mc),1.0) * vec4(step(rand(uv),DENSITY)) + fragColor, 1.0);
    }
}
