float uvMult = 2.;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	uv*=uvMult;
    float len = length(uv - vec2(.5 * uvMult)) * 50.;    
    vec2 s = normalize(uv) * ( sin((len - iTime * 10.))) * .01;      
    vec4 tex = texture(iChannel0, uv + s);       
    fragColor = tex;

}
