void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = 3.1415;
    float tau = 2. * pi;
    float xt = iTime * 0.25;
	vec2 uv = abs(fragCoord.xy / iResolution.xy - .5) * 2.0;
    uv.x *= iResolution.x / iResolution.y;

    float z = 1.5 - 1.5 * dot(uv, uv) + .1;
    uv /= z;

    float fx =  .4 * (cos(tau * xt) * sin(tau * uv.x) + 
        sin(tau * (cos(tau * xt) * uv.x * uv.y) / 2.))
        * (cos (pow(uv.y, 2.) * tau) + tan(tau * xt));
    float f = 1. - step(0.5,  abs(uv.y * uv.x - fx));
    float fv = 1. - step(0.5,  abs(pow(uv.y * uv.x, 2.) - fx));
    float fw = 1. - step(0.2,  abs(uv.y - fx));


	fragColor = (f * vec4(0.3, 0.6, 0.8, 1.) +
        	    fv * fw * vec4(0.6, 0.5, 0.2, 1.)) * (1. - f) +
        		(1. - fv) * vec4(0.6, 0.1, 0.4, 1.) + 
        		(1. - fw) * vec4(0.2, 0.1, 0.4, 1.) + 
        		(f * fv * fw) * vec4(1., .3, 0.3, 1.);
}
