void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = texture(iChannel0, uv).xyz;

    float average = (color.x + color.y + color.z) / 3.0;

    color = mix(color, vec3(1,0,0), uv.x);

    vec3 gray = vec3(average);
    float treshold = ((sin(iTime)+1.0)/2.0) * uv.x;

    if(average < treshold){
        color = vec3(0.0);
    }


	fragColor = vec4(color,1.0);
}
