#define iter 200.0 // Iterations
#define zoom   2.0 // Zoom
#define shouldDrawMandel false // Mandelbrot

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 colour = vec3(1,1,1); vec2 c = vec2(-0.4,0.6);
	vec2 z = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.xx * zoom;
	if (shouldDrawMandel) { c = z; z = vec2(0,0); }

	for(float i=0.0;i<iter;i+=1.0)
	{
		// z = z^2 + c
		// z^2 = (a^2 - b^2 + 2abi)
		z = vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y)+c;

		if (z.x * z.x + z.y * z.y > 4.0) { colour = vec3(i/iter * 0.4, i/iter  * 0.6, i/iter); break; }
	}

	fragColor = vec4(3.0*colour*vec3(fragCoord.xy/iResolution.xy * 2.0,1)+vec3(0.07, 0.07, 0.07),1.0);
}
