void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 center = vec2(iResolution.x * 0.5, iResolution.y * 0.5)/iResolution.yy;

	vec2 uv = fragCoord.xy / iResolution.yy - center;
	vec2 normal = normalize(iMouse.xy/iResolution.yy - center);

	mat2 rotation = mat2(normal.x, normal.y, //rotation mtx
					 	 -normal.y,  normal.x);

	//float t = iTime * 0.75;
	//mat2 rotation = mat2(	cos(t), 	sin(t),
	// 				      	-sin(t), 	cos(t));


	vec2 coord = uv * rotation +center;

	fragColor = texture(iChannel0,coord);

}
