vec4 makeBackground(in vec2 fragCoord, in float iTime) {
 	vec2 xy = fragCoord.xy;
    vec2 normxy = vec2(xy.x / iResolution.x, xy.y / iResolution.y);

    vec4 color = vec4(1.0,1.0,1.0,1.0);

    //color.r = xy.y;

    color.r = normxy.x/2.0 + normxy.y/2.0;   
    color.g = normxy.y/1.5;
    color.b = 2.0/normxy.x + 2.0/normxy.y;

    float amp = sin(0.5 * iTime);

    float thing = (amp* sin(0.1 * xy.x) + amp* cos(0.1 *xy.y))/2.0 + 1.0;

    color.r *= thing;
   	color.g *= thing;
    color.b *= thing; 

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy/ iResolution.xy;//Condensing this into one line
    vec4 texColor = texture(iChannel0, xy);//Get the pixel at xy from iChannel0

    vec4 bgColor = makeBackground(fragCoord, iTime);

    float avg = (texColor.r + texColor.g + texColor.b)/3.0;

    if (texColor.g > 0.6 && texColor.r < 0.3) {
        texColor = bgColor;
    }

    fragColor = texColor;//Set the screen pixel to that color
}
