void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 nuv = uv * 2.0 - 1.0;

    float bpm = 1.0 / 60.0 * 110.0;
    float t = iTime;
    float bt = t * bpm;
    float pi = 3.14159265359;
    float btp = bt * pi;

    float ts = 1.0 / 32.0;
    float t0 = texture(iChannel0, vec2(ts * 0.0, 0.25)).x;
    float t1 = texture(iChannel0, vec2(ts * 1.0, 0.25)).x;
    float t2 = texture(iChannel0, vec2(ts * 3.5, 0.25)).x;

    vec3 c = vec3(0.0);

    vec3 colb = vec3(0.0, 0.13, 0.23);
    vec3 colf = vec3(0.58, 0.06, 0.02);
    vec3 col0 = vec3(0.10, 0.43, 0.16);
    vec3 col1 = vec3(0.24, 0.82, 0.66);
    vec3 col2 = vec3(0.18, 0.16, 0.22);

    float rtuv = 1000.0 + btp * 0.1 + nuv.x * pow(uv.y - uv.y * 0.05, 1.2) * 14.0;

    mat2 ruv0 = mat2(cos(rtuv), -sin(rtuv), sin(rtuv), cos(rtuv * nuv.y));
    mat2 ruv1 = mat2(cos(rtuv), -sin(rtuv), sin(rtuv), cos(rtuv));
    vec2 tuv = ((nuv * ruv0) + (nuv * ruv1)) * 0.5;

    float n0 = texture(iChannel2, tuv).x;
    float n1 = texture(iChannel2, nuv).x;

    float txp = abs(cos(btp * 0.1) * sin(btp * 0.2) * tuv.x * tuv.y) + n0 * 0.1;

    float txn = texture(iChannel1, tuv).x * mix(1.0, 1.2 + pow(t2, 3.0) * 3.0 * nuv.y, n1);
    float tx0 = texture(iChannel1, nuv + vec2(0.0, bt)).x + n0;

    c.xyz = colb + colb * pow(abs(nuv.y), 2.0) * 1.5;
    c.xyz -= pow(abs(nuv.y + 0.05), 2.0) * 0.63 - sin(uv.x * 12.0 + btp) * 0.01 + (ruv1 * vec2(tx0 + sin(btp))).x * 0.00001;
    c.xyz -= pow(abs(nuv.y + 0.05), 2.0) * -0.13 - sin(uv.y * 2.0 + btp) * 0.19 * (c.y) * tx0 * n0;
    c.xyz += pow(abs(nuv.y + 0.3 + sin(btp * 0.1)), 2.0) * 0.2 * sin(uv.x * 7.0 + sin(btp) * 0.1) * colf;
    c.xyz += col1 * t2 * 0.5 * length(c.xyz) + 0.1;

    c.xyz = mix(c, col0, txp * pow(t0 + 0.2, 4.5) * 3.0);
    c.xyz = mix(c, col1, txn * pow(t1, 0.5) * (txp + 0.05) * 2.0);

    c.xyz += colf * t2 * 0.31 + pow(t0, 4.0) * abs(nuv.y);

    fragColor.xyz = c;
}
