vec3 firework(vec2 r, vec3 col, float GlobalTime){
    vec3 ret = vec3(0.0);
	vec2 r1 = r + vec2(0.0, pow(GlobalTime - 1.0, 2.0) * 0.2 - 0.8);
    if(mod(length(r1), 0.02 * GlobalTime) <= 0.02 && mod(atan(r1.y / r1.x), 3.141592 / 16.0) <= 3.141592 / 64.0 && GlobalTime * 0.2 >= length(r1)){
    	ret = col * ((2.0 - GlobalTime) / 2.0 + 0.5) 
                * max(0.,(.5+.5*sin(atan(r1.y , r1.x)*2.*16.))*4.-3.) * 1.5;
    }
    return ret;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //def
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    vec3 ret = vec3(0.0);
    vec3 col = vec3(0.8, 0.2, 0.2);
    vec3 col1 = vec3(0.2, 0.8, 0.2);
    vec3 col2 = vec3(0.2, 0.2, 0.8);

    //fireworks
    ret += firework(r - vec2(-0.8, -0.7), col1, mod(iTime - 2.0, 9.0));
    ret += firework(r - vec2(1.2, -0.3), col2, mod(iTime - 2.0, 9.0) - 2.0);
    ret += firework(r - vec2(-0.5, 0.2), col, mod(iTime - 2.0, 9.0) - 4.0);
    ret += firework(r - vec2(0.6, -1.0), col2, mod(iTime - 2.0, 9.0) - 6.0);

    //moon
    if(length(r - vec2(-1.65, 0.9)) <= 0.2){
    	ret = vec3(0.7);
    }
    if(length(r - vec2(-1.51, 0.9)) <= 0.05){
    	ret = vec3(0.5);
    }
    if(length(r - vec2(-1.52, 0.87)) <= 0.05){
    	ret = vec3(0.5);
    }
    if(length(r - vec2(-1.54, 0.85)) <= 0.05){
    	ret = vec3(0.5);
    }
    if(length(r - vec2(-1.65, 0.77)) <= 0.05){
    	ret = vec3(0.5);
    }

	fragColor = vec4(ret,1.0);
}
