//#define pi 3.1415926
float pi = 3.1415926;
float pi2 = 2.0 * 3.1415926;

vec4 rnd(vec2 pos)
{
    return texture(iChannel0, pos);
}

float wave(vec2 uv)
{
    float m = 0.0;
    float sum = 0.0;
    const float n = 4.0;
    for(float y = 1.0; y <= n; y++)
    {
        for(float x = 1.0; x <= n; x++)
        {
            //vec4 r = rnd(0.001 * iTime + vec2(x, y) / n);
            vec4 r = rnd(vec2(x, y) / n);
            r.z = 2.0 * r.z - 1.0;
            float a = sin(pi2 * (x * uv.x + r.x + 0.1 * iTime * r.z));
            float b = sin(pi2 * (y * uv.y + r.y + 0.1 * iTime * r.z));
            sum += a * b / (x * y);
            m += 1.0 / (x * y);
        }
    }
    return sum / m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 d = 1.0 / iResolution.xy;
    vec3 nrm;
    nrm.x = -wave(uv + vec2(-d.x, 0.0)) - wave(uv + vec2(+d.x, 0.0));
    nrm.y = -wave(uv + vec2( 0.0,-d.y)) - wave(uv + vec2( 0.0,+d.y));
    nrm.z = 1.0;
    nrm = normalize(nrm);

    vec3 r = refract(vec3(0.0, 0.0, 1.0), nrm, 1.0);
    fragColor = texture(iChannel1, uv.xy + 0.01 * r.xy);
    //fragColor = texture(iChannel1, uv.xy);
    fragColor.rgb += pow(abs(r.z), 100.0);
}
