void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; //skærmstørrelse laves til forhold i stedet for eksagte størrelser

    vec3 rod = vec3(1.0,0,0); //Rød, til firkant

    vec3 sort = vec3(0); //farve sort til baggrund

    vec3 pen = sort;

    float d = abs(uv.x-uv.y); 

    float e = abs(uv.x + uv.y - 1.0);


    if (d<0.002 || e < 0.002) {
     	pen = rod;   
    }





    fragColor = vec4(pen,1.0);

}
