void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = -1.0+2.0*fragCoord.xy/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;
	uv.x += mod(iTime/4.0, 8.0);
	uv = mod(uv, 2.0)-1.0;

	for (int i = 0; i < 4; ++i) {
		uv = vec2(uv.x*uv.x-uv.y*uv.y, 2.0*uv.x*uv.y);
		uv = -1.0+2.0*abs(uv);
	}

	vec3 n = normalize(vec3(2.0*uv.x, 2.0*uv.y, dot(uv, uv) - 1.0));
	vec3 a = vec3(0.5+0.2*cos(uv.x), 0.7+0.3*sin(uv.y), 0.6+0.4*cos(uv.x));
	vec3 b = vec3(0.7+0.3*uv.y/500000.0, 0.7+0.3*uv.y*uv.y/100000.0, 0.6+0.4*uv.x/5000.0);

	float i = max(0.0, dot(n, vec3(0.0, 0.0,  1.0)));
	float o = max(0.0, dot(n, vec3(0.0, 0.0, -1.0)));

	fragColor = vec4(vec3(sqrt(b*i+a*o)),1.0);
}
