*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy) * 2. - 1.;
	vec2 aspectRatio = vec2(iResolution.x / iResolution.y, 1.);
	float time = -iTime * 5.;
	fragColor = vec4( sin(length(uv * aspectRatio * .8) * 25. + time) );
}
