void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = abs(2.0 * (fragCoord.xy / iResolution.xy) - vec2( 1 )); 
	float ratio = iResolution.x / iResolution.y;
	p.x *= ratio;


	//Get some angle
	float a = atan( p.x, p.y );
	//Get radius
	float r = length( p );
	//wavy thing around it
	float w = pow( 1.0 + sin( 2.0*a ), 5.0)*0.008;

	float range = iResolution.y / 70.0;
	float scale = 4.0;
	if ( (r + w )< 0.9 ) {
		scale = -scale;
	}

#if 1
	//linear lines 
	r = p.x + p.y;
#endif

	float c = step( mod( scale * iTime + r*range, 1.0), 0.5 ); 

	fragColor = vec4( c);	
}
