const int lightCount = 12;

const vec2 midPos = vec2(0.0, 0.0);


vec3 light (vec2 r, in vec3 color, float factor) {
	return color / ((dot (r, r) + color) * factor);
}

vec3 drawItem(vec2 position, vec2 uv, float progress, vec3 tint, float rotation,
             float factor1, float strech) {
    float d;
    vec3 color = vec3(0.0);
    float rotationSpeed = -iTime * rotation * 3.0;
    for(int i = 0; i < lightCount; i++){
        float r = (float(i) * 6.28 + rotationSpeed) / float(lightCount);
        d = progress * progress * progress * progress;
        vec2 pos = uv - vec2 (cos(r) * d * 3.5 * strech, sin(r) * d);
        pos += position;
		color += light (pos, tint * d * 0.55, d * 0.03 * (factor1));
	}
    return color;
}

vec3 drawRing(vec2 uv, float progress, vec3 tint, float rotation, float factor1, float strech) {
    vec3 color = vec3(0.0);
	float rotationSpeed = -iTime * rotation;

	for(int i = 0; i < lightCount; i++){
		float r = (float(i) * 6.28 + rotationSpeed) / float(lightCount);
		vec2 pos = vec2 (cos(r) * progress * 40.0 * strech, sin(r)* progress * 20.0);
        pos -= midPos;
    	color += drawItem(pos, uv, progress, tint, rotation, factor1, strech);
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy / iResolution.xy-0.5) * vec2 (500, 500);
    vec3 color = vec3(0.0);
    float progress1 = (sin(iTime) + 1.0) * 1.5;
    float progress2 = (sin(iTime + 1.57) + 1.0) * 1.5;
    float progress3 = (sin(iTime + 3.14) + 1.0) * 1.5;
    float progress4 = (sin(iTime + 4.71) + 1.0) * 1.5;

    float factor1 = sin((mod(iTime, 5.0) / 5.0) * 6.28) + 0.0001;
    float factor2 = (sin((mod(iTime + 2.0, 12.0) / 12.0) * 6.28) + 1.0) * 0.5 + 0.005;

	color += drawRing(uv, progress1, vec3(0.1, 0.05, 0.3), 	10.0, factor1, 	1.0);
    color += drawRing(uv, progress2, vec3(0.1, 0.5, 0.03), 	-10.0, 1.0,		0.25);
    color += drawRing(uv, progress3, vec3(0.5, 0.0, 0.01), 	-5.0, 1.0,		0.75);
    color += drawRing(uv, progress4, vec3(0.3, 0.3, 0.0), 	5.0, factor2,	0.5);
    fragColor = vec4 (color, 1.0);
}
