#define NUM_BALLS 5
#define STRAT 4.0
#define WIDTH 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = -1.0 + 2.0 * uv;
    vec2 pos;
    float t = iTime;
    float sum = 0.0;
    for( int i = 0; i < NUM_BALLS; i++ )
    {
        float st = sin( t );
        float fi = float(i);

    	pos = 0.5*vec2( 0.5*st + 0.5*cos(t+fi)+cos( t + float(i)), cos ( (1.0+0.5*fi)*t +st + float(i)));
        float d = 0.02/((pos.x-uv.x)*(pos.x-uv.x) + (pos.y-uv.y)*(pos.y-uv.y) );
        sum+=d;
    }
    sum = floor(STRAT*sum)/STRAT;
    sum = step(WIDTH,sum)*sum;
    vec2 mPos = iMouse.xy/iResolution.xy; mPos.x = 1.0-mPos.x;
    vec3 col = vec3(1,1,0)-vec3(mPos,0);
	fragColor = vec4(sum*col + vec3(0.7-0.2*length(uv))*step(sum,0.01)*col,1.0);
}
