void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 m = vec2(iMouse.x, iMouse.y) / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    float rad = 1.8;

    vec2 pos = m;
    if(m.x < 0.01 && m.y < 0.1)
        pos = vec2(0.5);

    float s = 5.0;
    float dx = (1.4 + sin(-iTime*s)*0.1 );
    float dy = (1.4 + cos(iTime*s*1.3)*0.1);
    vec2 dxy = vec2(dx, dy) * 1.1;

    dxy *= vec2(ratio, 1.0);
    float dist = distance(pos * dxy, uv * dxy) * 5.0;
    float hole = 10.0 - pow(dist * (1.0/rad), 15.0);

    float f1 = (1.0 + sin(iTime/3.))/2.0;
    uv.y = 1.0-uv.y;
    vec4 col = texture(iChannel0, uv);
    vec4 col_back = vec4(dot(col.xyz, vec3(1.)) / (2.0 + 1.3*f1) );

	fragColor = mix(col, col_back, hole);
}
