#define PI 3.1415926536

//==============================================
// Projections
//==============================================

vec3 toWorldEqui(in vec2 xy)
{
    float theta = 2.0 * PI * xy.x;
    float phi   = PI * (1.0 - xy.y);
    return vec3(-sin(theta)*sin(phi), -cos(phi), cos(theta)*sin(phi));
}

//==============================================
// Main
//==============================================

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy;
    vec3 XYZ = toWorldEqui(xy/iResolution.xy);
    fragColor = texture(iChannel0, XYZ);

    float grey = 1.0/3.0*(fragColor.r+fragColor.g+fragColor.b);
    fragColor.r = 5.0*abs(dFdx(grey));
    fragColor.b = 5.0*abs(dFdy(grey));
    fragColor.g = 0.0;
}
