#define COLORED 1


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 center = floor(fragCoord/6.0)*6.0 + 3.0; // size of dot
    vec3 col = texture(iChannel0, center/iResolution.xy).rgb;
    float l = 0.3; //max(0.1, dot(col, vec3(0.2125, 0.7154, 0.0721))); // size depends of color
    float dist = distance(center,fragCoord)/8.0;
    float alpha = smoothstep(1.0, 0.5, dist/l);
    #if defined COLORED
    	fragColor.rgb = col.rgb * alpha;
    #else
    	fragColor.rgb = vec3(alpha);
    #endif
}
