#define TAU 6.28318530718

float exponentialInOut(float t) {
  return t == 0.0 || t == 1.0
    ? t
    : t < 0.5
      ? +0.5 * pow(2.0, (20.0 * t) - 10.0)
      : -0.5 * pow(2.0, 10.0 - (t * 20.0)) + 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // params
    vec2 textureOffset = vec2(-0.5);
    float easedProgress = exponentialInOut(mod(iTime * 0.3, 1.));
    float rotation = easedProgress * TAU;
    float aspect = 5.32/3.;
    float zoom = 0.872;
    zoom += 0.6 * sin(rotation / 2.);

    // rotate
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = vec2((uv.x - 0.5) * (iResolution.x / iResolution.y), uv.y - 0.5);
	uv *= (2. - zoom) / 2.;
    uv *= mat2(cos(rotation), sin(rotation), -sin(rotation), cos(rotation));
    uv.y *= aspect;
    vec4 color = texture(iChannel0, uv - textureOffset);
    fragColor = color;
}
