#define T(x) smoothstep(0.,1.,texture(iChannel0,vec2( mod(x+iTime*.2,1.) ,0.2)).r*7.-c.w*6.)

void mainImage( out vec4 a, vec2 b )
{
	vec4 c = b.xyxy / iResolution.xyxy;

    c.x = length(c.zw); c.y = atan(.5 - c.w,.5 - c.z);

    // color gradient
	a = sin(
          cos(
              cos(  T(c.x) - c.y   )
              * T(c.z)
              * T(-c.x*.4)
              * 2.
              - iTime
          )
        * vec4(3.,5.,6.5,1.)
    );
}
