// testing the browser abilities : http://webglreport.com/?v=2

void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;
    #if __VERSION__ >= 300                    // webGL2
        O = vec4(0,1,0,0);
    #else                                     // webGL1
        if (U.x<.5)
            #ifdef GL_EXT_shader_texture_lod  // textureLOD
         // #ifdef GL_EXT_gpu_shader4         // texelFetch
                O = vec4(0,0,1,0);
            #else
                O = vec4(1,0,0,0);
            #endif
        else
            #ifdef GL_EXT_draw_buffers        // multiple buffers
                O = vec4(0,0,1,0);
            #else
                O = vec4(1,0,0,0);
            #endif
  #endif
}
