void mainImage( out vec4 O, vec2 v )
{   
    float t = iTime * -3.7,
         d = step( sin(t * .1), .0);
	v = abs( v / iResolution.y - vec2(d+d, .5) );
    float a = v.x + v.y,
          s = round(d*3.)*10.+2.,
          y = (1. + sin(a * s + t)) * .5;

    O += smoothstep(y, y + .001, (1. + cos(t)) * .5) -O;
}

vec2 mainSound( in int samp, float time )
{
    float t = sin(0.5+time*3.7)*10.0;
   float f = step(sin(0.5+time*3.7*2.0),0.5);
    return vec2( exp(290.0*t)*300.0-f );
}
