void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;
    float t = iTime;
    float b = 1.0 / 60.0 * 160.0;
    float pi = 3.1415926535;

    vec2 nuv0 = vec2(cos(t * b * pi / 2.0) * 2.5, sin(t * b * pi / 2.0) * 8.0);

    float beat = pow(abs(sin(t * b * pi)), 8.0);
    float intense = texture(iChannel0, vec2(0.0, 0.25)).x * 1.0;

    vec3 flashcol = vec3(0.2, 0.4, 0.6);

    fragColor.xyz += flashcol * pow(intense, 0.25) * pow(beat, 0.25) * abs(1.0 - length(nuv) * 15.0) * 0.1;

    float vocal = texture(iChannel0, vec2(0.15, 0.25)).x;
    vec3 vocalcol = vec3(0.1, 0.2, 0.2);

    fragColor.xyz += vocal * vocalcol * abs(1.0 - length(nuv.y * 20.0));
    fragColor.xz *= length(nuv0) * 2.0;
    fragColor.xz *= texture(iChannel1, nuv * pow(beat, 0.2) * 100.0 - intense).x * intense;

    fragColor.xyz += texture(iChannel0, vec2(uv.x, 0.75)).x * (0.2 + intense);
}
