const vec4 channelFilters[4] = vec4[4](
	vec4(1.0, 0.0, 0.0, 0.0),
	vec4(0.0, 1.0, 0.0, 0.0),
	vec4(0.0, 0.0, 1.0, 0.0),
	vec4(0.0, 0.0, 0.0, 1.0)
);

float getChannel(int channel, sampler2D tex, vec2 uv)
{
    return dot(texture(tex, uv), channelFilters[channel]);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float channel = getChannel(2, iChannel0, uv);
	fragColor = vec4(vec3(channel), 1.0);
}
