void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 halfRes = iResolution.xy / 2.0;
    vec2 updatedFragCoord = fragCoord + vec2(cos(iTime * 2.0) * 70.0, sin(iTime * 2.0) * 70.0);
    float dist = distance(updatedFragCoord, halfRes);
    vec2 final = (1.0 - (dist / halfRes));
    fragColor = vec4(final.y, final.y, final.y, 1.0);
}
