void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy,
          p = iMouse.xy;
    if (iMouse.w < .5) {
        p = iResolution.xy * vec2(.5, .1);
    }

    float d = length(p - uv.xy) - uv.y,
          r = clamp(d, 0., 1.);
    vec3 c = vec3(r, 0, 0);
	fragColor = vec4(c, 1.);
}
