void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y = 1.0 - uv.y; //pone derecha la imagen

	vec2 cambiar = vec2(-0.0001, 0.0001); //movimiento minimo para capturar bordes
	vec4 principal;
	vec4 secundaria;
	vec4 aux;

	principal = texture(iChannel1, uv); //iChannel1 debe estar vacÃ­a
	secundaria = principal;
	secundaria = texture(iChannel0, uv + cambiar); //iChannel1 debe contener la imagen
	aux = texture(iChannel0, uv - cambiar);

	//principal = principal - secundaria; //quitamos la segunda imagen
	principal = principal + secundaria;
	principal = principal + aux;

	vec4 grad = principal;
	fragColor = vec4(grad);

}
